CREATE PROC UpdateNhanVien @MANV VARCHAR(10),@TENTK VARCHAR(10),@TENNV NVARCHAR(40),@CMND_CCCD VARCHAR(12),@NGAYSINH DATETIME, @GIOITINH NVARCHAR(3),
@SDT VARCHAR(12), @DIACHI NVARCHAR(MAX),@CHUCVU NVARCHAR(20),@HINHANH IMAGE,@NGAYVAOLAM DATETIME,@TRANGTHAI BIT
AS
BEGIN
UPDATE NhanVien 
SET TENTK=@TENTK, TENNV=@TENNV,CMND_CCCD=@CMND_CCCD,NGAYSINH=@NGAYSINH,GIOITINH=@GIOITINH,SDT=@SDT,DIACHI=@DIACHI,CHUCVU=@CHUCVU,HINHANH=@HINHANH,NGAYVAOLAM=@NGAYVAOLAM,TRANGTHAINV=@TRANGTHAI
WHERE MANV=@MANV
END
GO
CREATE PROC updateStatusNhanVien @MANV VARCHAR(10),@TRANGTHAI BIT
AS
BEGIN
UPDATE NhanVien
SET TRANGTHAINV=@TRANGTHAI
WHERE MANV=@MANV
END
GO
CREATE PROC insertNhanVien @MANV VARCHAR(10),@TENTK VARCHAR(10),@TENNV NVARCHAR(40),@CMND_CCCD VARCHAR(12),@NGAYSINH DATETIME, @GIOITINH NVARCHAR(3),
@SDT VARCHAR(12), @DIACHI NVARCHAR(MAX),@CHUCVU NVARCHAR(20),@HINHANH IMAGE,@NGAYVAOLAM DATETIME,@TRANGTHAI BIT
AS
