INSERT NhanVien VALUES(@MANV,@TENTK,@TENNV,@CMND_CCCD,@NGAYSINH,@GIOITINH,@SDT,@DIACHI,@CHUCVU,@HINHANH,@NGAYVAOLAM,@TRANGTHAI)
END
